// Deterministic Static Proof Program
// Renders a simple pattern of geometric shapes

function setup() {
  createCanvas(800, 600);
  noLoop();
}

function draw() {
  background(20, 24, 30);
  randomSeed(SEED);

  // Draw grid of deterministic shapes
  const cols = 8;
  const rows = 6;
  const cellW = width / cols;
  const cellH = height / rows;

  for (let i = 0; i < cols; i++) {
    for (let j = 0; j < rows; j++) {
      const x = i * cellW + cellW / 2;
      const y = j * cellH + cellH / 2;
      const size = map(VAR[0], 0, 100, 10, 40);
      const hue = map(VAR[1], 0, 100, 120, 200);

      fill(hue, 180, 200);
      noStroke();

      if ((i + j) % 3 === 0) {
        ellipse(x, y, size, size);
      } else if ((i + j) % 3 === 1) {
        rectMode(CENTER);
        rect(x, y, size, size);
      } else {
        triangle(
          x, y - size/2,
          x - size/2, y + size/2,
          x + size/2, y + size/2
        );
      }
    }
  }

  // Add text with seed info
  fill(255);
  textSize(12);
  textFont('monospace');
  text('SEED: ' + SEED, 10, height - 10);
}
